import {Typography} from "@material-ui/core"
import {TextField} from "@material-ui/core"
import { useState } from "react"
import InputAdornment from '@material-ui/core/InputAdornment';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Paper';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';

function App() {
  const classes = useStyles();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  return <div style={{ textAlign: "center", marginTop: 40}}>
    <Typography variant="h2">Email Rocket ðŸš€</Typography>

    <form style= {{marginTop: 30}}>
      <TextField 
        id="standard-basic" 
        label="Email" 
        value = {email}
        onChange = {(e) => setEmail(e.target.value)}/>
      <br/>  

      <TextField 
        style={{ marginTop: 20 }}id="standard-password-input"
          label="Password"
          value = {password}
          onChange = {(e) => setPassword(e.target.value)}
          type="password"
          autoComplete="current-password" />

        <br/>
        <TextField 
          style={{ marginTop: 20 }}
          id="standard-textarea"
          label="Subject"
          value = {subject}
          onChange = {(e) => setSubject(e.target.value)}
          placeholder="Placeholder"
          multiline
        />
        <br/>
        <TextField 
          style={{ marginTop: 10 }}
          id="standard-textarea"
          label="Message"
          value = {message}
          onChange = {(e) => setMessage(e.target.value)}
          placeholder="Placeholder"
          multiline
          helperText={`Character count: ${message.length}`}
          error = {message.length>=1000}
        />
        <br/>
    </form>
    <Paper
      elevation={3}
      style={{ width: "35%", margin: "auto", marginTop: 20, padding: 20 }}
    >
      <Typography variant="subtitle1">
        Drag your .txt file of emails this area to import them.
      </Typography>
    </Paper>

    <Button
        variant="contained"
        color="default"
        className={classes.button}
        startIcon={<CloudUploadIcon />}
      >
        Upload
      </Button>
    </div>;
}

export default App;
